import re

def check_following(response: str) -> bool:
    # 将所有 useQuery({...}) 块找出
    pattern = r"useQuery\s*\(\s*{.*?}\s*\)"
    matches = list(re.finditer(pattern, response, re.DOTALL))

    if not matches:
        return False

    for match in matches:
        query_block = match.group(0)

        # 若是数组形式，如 useQuery([...])
        if re.search(r"useQuery\s*\(\s*\[", query_block):
            return False

        # 确保 queryKey 是数组
        if not re.search(r"queryKey\s*:\s*\[.*?\]", query_block, re.DOTALL):
            return False

        # 确保 queryFn 存在
        if not re.search(r"queryFn\s*:", query_block):
            return False

    return True

