import ast

def check_following(response):


    def extract_tool(text):
        pattern = r'\[([^]]+)\]'
        matches = re.findall(pattern, text)
        if not matches:
            return []
        try:
            tools = eval(f"[{matches[-1]}]")
            return tools
        except Exception as e:
            # print("error in extract_tool:", e)
            return []

    def validate_function_call(call_str, func_def):
        """
        验证函数调用是否符合函数定义

        :param call_str: 函数调用字符串，如 'search(query="test", recency_days=7)'
        :param func_def: 函数定义字典，包含name和takes_inputs
        :return: (is_valid, error_msg) 元组，表示是否有效及错误信息
        """
        # 1. 提取函数名和参数部分
        try:
            # 使用AST安全解析函数调用
            module = ast.parse(call_str.strip())
            if not isinstance(module, ast.Module) or len(module.body) != 1:
                return False, "Invalid statement"

            call_node = module.body[0]
            if not isinstance(call_node, ast.Expr) or not isinstance(call_node.value, ast.Call):
                return False, "Not a function call"

            # 获取函数名
            called_func_name = None
            if isinstance(call_node.value.func, ast.Name):
                called_func_name = call_node.value.func.id

            # 2. 检查函数名是否匹配
            if called_func_name != func_def['name']:
                return False, f"Function name mismatch. Expected '{func_def['name']}', got '{called_func_name}'"

            # 3. 提取调用参数
            provided_args = {}
            for kw in call_node.value.keywords:
                arg_name = kw.arg
                # 获取参数值（处理基本类型的字面量）
                if isinstance(kw.value, ast.Constant):
                    arg_value = kw.value.value
                elif isinstance(kw.value, ast.Num):  # Python < 3.8
                    arg_value = kw.value.n
                elif isinstance(kw.value, ast.Str):  # Python < 3.8
                    arg_value = kw.value.s
                elif isinstance(kw.value, ast.NameConstant):  # Python < 3.8
                    arg_value = kw.value.value
                else:
                    arg_value = None  # 复杂表达式暂不处理

                provided_args[arg_name] = arg_value

            # 4. 验证参数
            expected_args = func_def['takes_inputs']
            errors = []

            # 检查必填参数
            for arg_name, arg_def in expected_args.items():
                if arg_def['required'] == 'True' and arg_name not in provided_args:
                    errors.append(f"Missing required argument: '{arg_name}'")

            # 检查未知参数
            for arg_name in provided_args:
                if arg_name not in expected_args:
                    errors.append(f"Unexpected argument: '{arg_name}'")

            # 检查参数类型
            for arg_name, arg_value in provided_args.items():
                if arg_name in expected_args:
                    expected_type = expected_args[arg_name]['type']
                    actual_type = type(arg_value).__name__

                    # 简单类型检查
                    if expected_type == 'int' and not isinstance(arg_value, int):
                        errors.append(f"Argument '{arg_name}' should be {expected_type}, got {actual_type}")
                    elif expected_type == 'str' and not isinstance(arg_value, str):
                        errors.append(f"Argument '{arg_name}' should be {expected_type}, got {actual_type}")

            if errors:
                return False, " | ".join(errors)

            return True, "Valid call"

        except Exception as e:
            return False, f"Parsing error: {str(e)}"

