    test_calls =  extract_tool(response)
    # print(test_calls)
    for call in test_calls:
        Flag = False
        for function_def in available_functions:
            is_valid, msg = validate_function_call(call, function_def)
            # print(function_def["name"], msg)
            if is_valid == True:
